// Assemble a single JSON from prior node outputs
const totals = $items('Postgres - Totals 30d')[0].json;
const reminders = $items('Postgres - Reminders 30d').map(i => i.json);
const series = $items('Postgres - Timeseries 30d').map(i => i.json);
const lead = $items('Postgres - Lead Time Hrs')[0].json;
const today = $items('Postgres - Today Snapshot')[0].json;

const confirmedRate = totals.total ? Math.round((totals.confirmed / totals.total) * 100) : 0;
const noShowRate = totals.total ? Math.round((totals.no_show / totals.total) * 100) : 0;

return [{
  json: {
    totals30d: totals,
    kpis: {
      confirmedRatePct: confirmedRate,
      noShowRatePct: noShowRate,
      avgHoursToAppt: lead.avg_hours_to_appt ?? null
    },
    reminders30d: reminders,
    timeseries30d: series,
    today: today,
    generatedAt: new Date().toISOString()
  }
}];
